import { createFeature, createReducer, on } from '@ngrx/store';
import { createSelector } from '@ngrx/store';
import { getOrCreateSharedStore } from '../../../../../../shared/store';
import * as SharedStateActions from './shared-state.actions';
const sharedStore = getOrCreateSharedStore();
const initialState = sharedStore.getState();
const reducer = createReducer(initialState, on(SharedStateActions.syncFromExternal, (_state, {
  state
}) => ({
  ...state
})), on(SharedStateActions.selectSection, (state, {
  sectionId
}) => ({
  ...state,
  selectedSection: sectionId
})), on(SharedStateActions.updateForm, (state, {
  changes
