  ...state,
  form: {
    ...state.form,
    ...changes
  }
})));
export const sharedStateFeature = createFeature({
  name: 'sharedState',
  reducer,
  extraSelectors: ({
    selectSharedState
  }) => ({
    selectSelectedSection: createSelector(selectSharedState, state => state.selectedSection),
    selectForm: createSelector(selectSharedState, state => state.form)
  })
});
export const {
  name: sharedStateFeatureKey,
  reducer: sharedStateReducer,
  selectSharedState,
  selectSelectedSection,
  selectForm: selectSharedForm
} = sharedStateFeature;
